import * as api from "../api";

//Action Creators are functions that return actions
//creating action creators
//action is an object which has type and payload

export const getPosts = () => async (dispatch) => {
  try {
    const { data } = await api.fetchPosts(); //we are first getting a response from api and in resonse we have data as object
    dispatch({ type: "FETCH_ALL", payload: data }); // payload: are data where we store all our posts
  } catch (error) {
    console.log(error.message);
  }
};

export const createPost = (post) => async (dispatch) => {
  //dispatch comes from redux thunk
  try {
    const { data } = await api.createPost(post); // we have first distructure the response as {data}, then we are making a post api request to our backend server. We are sending a post there.
    dispatch({
      type: "CREATE",
      payload: data,
    });
  } catch (error) {
    console.log(error.message);
  }
};

export const updatePost = (id, post) => async (dispatch) => {
  try {
    const { data } = await api.updatePost(id, post);
    dispatch({
      type: "UPDATE",
      payload: data,
    });
  } catch (error) {
    console.log(error.message);
  }
};
